    async netEarnings() {
      const itemConsultants = this.parentData.map(v => {
        return v.co_usuario;
      });
      const request = {
        item_consultants: itemConsultants
      };

      try {
        const response = await this.$store.dispatch("consultant/CONSULTANT_NET_EARNINGS", request); //response service

        const dataConsultan = response.data;
        const listSelect = this.parentData;
        const organizedData = dataConsultan.reduce(function (acumulador, valorActual) {
          const name = listSelect.find(consultant => {
            return consultant.co_usuario === valorActual.co_usuario;
          });
          let valorActualAssignName = Object.assign({
            name: name.no_usuario
          }, valorActual);
          acumulador[valorActual.co_usuario] = acumulador[valorActualAssignName.co_usuario] || [];
          acumulador[valorActual.co_usuario].push(valorActualAssignName);
          return acumulador;
        }, Object.create(null));
        let ObjectBar = [];
        let sumTotal = 0;
        let countMaxMonth = 0;

        for (let key in organizedData) {
          let earningsData = [];
          let sizeArray = organizedData[key].length;
          moment.months().map(month => {
            let busqueda = organizedData[key].find(index => {
              return month == moment(index.month).format('MMMM');
            });

            if (busqueda != undefined) {
              earningsData.push(this.numFormat(busqueda.total_net_earnings));
            } else {
              earningsData.push(0);
            }
          });
          let dataPrepare = {
            name: organizedData[key][0].name,
            type: 'column',
            data: earningsData
          };
          ObjectBar.push(dataPrepare);
          sumTotal += this.finalOperation(organizedData[key], 'fixed_cost');
          console.log(this.finalOperation(organizedData[key], 'fixed_cost'));

          if (sizeArray > countMaxMonth) {
            countMaxMonth = sizeArray;
          }
        }

        let totalConsultant = Object.keys(organizedData).length;
        let resulAverage = sumTotal / totalConsultant;
        let average = Array(12).fill(this.numFormat(resulAverage));
        console.log(resulAverage, this.numFormat(resulAverage)); // console.log(countMaxMonth)

        ObjectBar.push({
          name: 'Promedio',
          type: 'line',
          data: average
        }); //27,901 
        //18,22525

        this.dataGraph = ObjectBar; // console.log(this.dataGraph)
        // let col = 
        // console.log(this.numFormat(18.22525))
      } catch (error) {
        console.log(error);
      }
    },

    finalOperation(arrayObjectcs, key, type = true) {
      if (!Array.isArray(arrayObjectcs)) {
        return null;
      }

      let j = 1;
      let total = arrayObjectcs[0][key];

      if (!total) {
        return 0;
      }

      while (j < arrayObjectcs.length) {
        if (type) {
          total += arrayObjectcs[j][key];
        } else {
          total -= arrayObjectcs[j][key];
        }

        j++;
      }

      return total;
    },

    numFormat(item) {
      return new Intl.NumberFormat('de-DE').format(item.toFixed(2));
    }

