import VueApexCharts from "vue-apexcharts";
import { organizeByConsultants } from "../assets/util/util";
import { calculationsOfAmounts } from "../assets/util/util";
export default {
  props: ['parentData'],
  components: {
    apexchart: VueApexCharts
  },
  data: () => ({
    status: true,
    loading: false,
    series: [],
    chartOptions: {
      chart: {
        width: 380,
        type: 'pie'
      },
      labels: [],
      responsive: [{
        breakpoint: 480,
        options: {
          chart: {
            width: 200
          },
          legend: {
            position: 'bottom'
          }
        }
      }]
    }
  }),
  watch: {
    parentData() {
      if (this.parentData.length > 0) {
        this.initialize();
      } else {
        this.series = [];
        this.$emit("resetComponentValue", '');
      }
    },

    loading(val) {
      if (!val) return;
      setTimeout(() => this.loading = false, 10000);
    }

  },

  created() {
    this.initialize();
  },

  mounted() {
    organizeByConsultants();
    calculationsOfAmounts();
  },

  methods: {
    async initialize() {
      this.loading = true;
      const itemConsultants = this.parentData.map(v => {
        return v.co_usuario;
      });
      const request = {
        item_consultants: itemConsultants
      };

      try {
        const response = await this.$store.dispatch("consultant/CONSULTANT_NET_EARNINGS", request); //response service

        const dataConsultan = response.data;
        console.log(dataConsultan.length);

        if (!dataConsultan.length > 0) {
          this.$emit("resetComponentValue", '');
          this.series.splice(0, 4);
        }

        const itemsSelect = this.parentData;
        const dataOrganizeConsultant = organizeByConsultants(dataConsultan, itemsSelect);
        let sizeConsultant = Object.keys(dataOrganizeConsultant).length;
        this.series.splice(0, 4); // this.series.splice(0, sizeConsultant);

        let nameConsultan = [];

        for (let key in dataOrganizeConsultant) {
          let suma = calculationsOfAmounts(dataOrganizeConsultant[key], 'total_net_earnings');
          let total = parseInt(suma / dataOrganizeConsultant[key].length);
          nameConsultan.push(dataOrganizeConsultant[key][0].name);
          this.series.push(total);
        }

        this.$refs.radar.updateOptions({
          labels: nameConsultan
        });
        this.loading = false;
      } catch (error) {
        console.log(error);
      }
    },

    organizeByConsultants,
    calculationsOfAmounts
  }
};
