    parentData() {
      if (this.parentData.length > 0) {
        this.netEarnings();
      } else {
        this.dataGraphPizza = [];
        this.$emit("resetComponentValue", '');
      }
    }

  },

  created() {
    this.netEarnings();
  },

  methods: {
    async netEarnings() {
      const itemConsultants = this.parentData.map(v => {
        return v.co_usuario;
      });
      const request = {
        item_consultants: itemConsultants
      };

      try {
        const response = await this.$store.dispatch("consultant/CONSULTANT_NET_EARNINGS", request); //response service

        const dataConsultan = response.data;
        const listSelect = this.parentData;
        const organizedData = dataConsultan.reduce(function (acumulador, valorActual) {
          const name = listSelect.find(consultant => {
            return consultant.co_usuario === valorActual.co_usuario;
          });
          let valorActualAssignName = Object.assign({
            name: name.no_usuario
          }, valorActual);
          acumulador[valorActual.co_usuario] = acumulador[valorActualAssignName.co_usuario] || [];
          acumulador[valorActual.co_usuario].push(valorActualAssignName);
          return acumulador;
        }, Object.create(null));
        let dataSum = [];

        for (let key in organizedData) {
          let initialValue = 0;
          let suma = organizedData[key].reduce((previousValue, currentValue) => {
            return previousValue + currentValue.total_net_earnings;
          }, initialValue);
          console.log(suma, organizedData[key].length);
          let opr = parsethis.numFormat(suma / organizedData[key].length);
          dataSum.push({
            name: organizedData[key][0].name,
            total: opr
          });
        }

        console.log(dataSum);
      } catch (error) {
        console.log(error);
      }
    },

    numFormat(item) {
      return new Intl.NumberFormat('de-DE').format(item.toFixed(2));
    }

  }
};
