import moment from 'moment';

function finalOperation(arrayObjectcs, key, type = true) {

    if(!Array.isArray(arrayObjectcs)){
        return null;
    }

    let total = 0;

    total = arrayObjectcs.reduce((previousValue, currentValue, i) => {
        if(type)
        {
            return previousValue + currentValue[key];
        }
        else{
            if( i != 0 )
            { 
                previousValue -= currentValue[key];
            }else{
                previousValue = currentValue[key];
            }
            return previousValue;
        }
    }, 0);

    return this.numFormat(total);
}

function numFormat(item) {
    return new Intl.NumberFormat('de-DE').format(item.toFixed(2));
}

function formatMonth(params){
    return moment(params).format('MMMM')
}
